    document.querySelectorAll('.anchor-link').forEach(link => {
        link.addEventListener('click', function(e) {
            e.preventDefault(); // –û—Ç–º–µ–Ω—è–µ–º —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–æ–µ –ø–æ–≤–µ–¥–µ–Ω–∏–µ —Å—Å—ã–ª–∫–∏

            const targetId = this.parentElement.getAttribute('id'); // –ü–æ–ª—É—á–∞–µ–º id –∑–∞–≥–æ–ª–æ–≤–∫–∞ h3
            const targetElement = document.getElementById(targetId); // –ù–∞—Ö–æ–¥–∏–º —ç–ª–µ–º–µ–Ω—Ç –ø–æ id

            // –ü–ª–∞–≤–Ω—ã–π —Å–∫—Ä–æ–ª–ª–∏–Ω–≥ –∫ —Ü–µ–ª–µ–≤–æ–º—É —ç–ª–µ–º–µ–Ω—Ç—É
            targetElement.scrollIntoView({
                behavior: 'smooth', // –ü–ª–∞–≤–Ω–∞—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∞
                block: 'start'     // –í—ã—Ä–∞–≤–Ω–∏–≤–∞–Ω–∏–µ –ø–æ –≤–µ—Ä—Ö–Ω–µ–π —á–∞—Å—Ç–∏ –æ–∫–Ω–∞
            });
        });
    });

    // –õ–æ–≥–∏–∫–∞ –≤–∏–∫—Ç–æ—Ä–∏–Ω—ã (–æ—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å)
    const timeouts = {};

    function checkAnswer(button, selectedAnswer, sectionId) {
        const correctAnswers = {
            'what-are-binary-options': 'c',
            'how-to-trade-binary': 'a',
            'advantages': 'b',
            'example': 'b',
            'strategies': 'c',
            'risks': 'd',
            'assets': 'c',
            'platforms': 'b',
            'tips': 'c',
            'why-zebrarium': 'c'
        };
        const correctAnswer = correctAnswers[sectionId];
        const resultDiv = document.getElementById(`result-${sectionId}`);
        const timerDiv = document.getElementById(`timer-${sectionId}`);
        const progressBar = document.getElementById(`progress-${sectionId}`);
        const buttons = document.querySelectorAll(`#${sectionId} .quiz-option`);

        if (timeouts[sectionId]) return;

        if (selectedAnswer === correctAnswer) {
            resultDiv.textContent = "–£ –≤–∞—Å –æ—Ç–ª–∏—á–Ω—ã–µ –∑–Ω–∞–Ω–∏—è! –ú–æ–∂–Ω–æ –ø–µ—Ä–µ—Ö–æ–¥–∏—Ç—å –∫ –ø—Ä–∞–∫—Ç–∏–∫–µ.";
            resultDiv.className = "quiz-result success";
            resultDiv.innerHTML += '<br><a href="https://zebrarium.ru/register/" class="button">–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è</a>';
            buttons.forEach(btn => btn.disabled = true);
        } else {
            resultDiv.textContent = "–ó–Ω–∞–Ω–∏–π –ø–æ–∫–∞ –Ω–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ. –ù—É–∂–Ω–æ –µ—â–µ —Ä–∞–∑ –≤–Ω–∏–º–∞—Ç–µ–ª—å–Ω–æ –ø—Ä–æ—á–∏—Ç–∞—Ç—å —Å—Ç—Ä–∞–Ω–∏—Ü—É. –°–ª–µ–¥—É—é—â–∞—è –ø–æ–ø—ã—Ç–∫–∞ –æ—Ç–≤–µ—Ç–∞ —á–µ—Ä–µ–∑ 3 –º–∏–Ω—É—Ç—ã.";
            resultDiv.className = "quiz-result error";
            let timeLeft = 180;
            buttons.forEach(btn => btn.disabled = true);

            timeouts[sectionId] = setInterval(() => {
                timeLeft--;
                const minutes = Math.floor(timeLeft / 60);
                const seconds = timeLeft % 60;
                timerDiv.textContent = `–û—Å—Ç–∞–ª–æ—Å—å: ${minutes}:${seconds < 10 ? '0' + seconds : seconds}`;
                const progressWidth = ((180 - timeLeft) / 180) * 100;
                progressBar.style.width = `${progressWidth}%`;

                if (timeLeft <= 0) {
                    clearInterval(timeouts[sectionId]);
                    timeouts[sectionId] = null;
                    resultDiv.textContent = "";
                    timerDiv.textContent = "";
                    progressBar.style.width = "0%";
                    buttons.forEach(btn => btn.disabled = false);
                }
            }, 1000);
        }
    }
</script>
